        uint256 entryAmount,
        uint256 maxParticipants,
        uint256 poolDeadline
    ) external returns (address) {
        // Deploy new pool contract
        LuckyDrawPool pool = new LuckyDrawPool(
            usdtToken,
            tlootToken,
            supraRouter,
            admin,
            eventName,
